        param.setValue("//\n//\n//                          MMMMMMMMMMMMMMMMMMMMMMMMMMMM\n//                        MM.                          .MM\n//                       MM.  .MMMMMMMMMMMMMMMMMMMMMM.  .MM\n//                      MM.  .MMMMMMMMMMMMMMMMMMMMMMMM.  .MM\n//                     MM.  .MMMM        MMMMMMM    MMM.  .MM\n//                    MM.  .MMM           MMMMMM     MMM.  .MM\n//                   MM.  .MmM              MMMM      MMM.  .MM\n//                  MM.  .MMM                 MM       MMM.  .MM\n//                 MM.  .MMM                   M        MMM.  .MM\n//                MM.  .MMM                              MMM.  .MM\n//                 MM.  .MMM                            MMM.  .MM\n//                  MM.  .MMM       M                  MMM.  .MM\n//                   MM.  .MMM      MM                MMM.  .MM\n//                    MM.  .MMM     MMM              MMM.  .MM\n//                     MM.  .MMM    MMMM            MMM.  .MM\n//                      MM.  .MMMMMMMMMMMMMMMMMMMMMMMM.  .MM\n//                       MM.  .MMMMMMMMMMMMMMMMMMMMMM.  .MM\n//                        MM.                          .MM\n//                          MMMMMMMMMMMMMMMMMMMMMMMMMMMM\n//\n//\n//\n//\n// Adaptation pour Natron par F. Fernandez\n// Code original : crok_blobs Matchbox pour Autodesk Flame\n\n// Adapted to Natron by F.Fernandez\n// Original code : crok_blobs Matchbox for Autodesk Flame\n\n\n\nuniform float Speed = 5.0; // Speed : (speed)\nuniform float Offset = 0.0; // Offset : (offset), min=-1000, max=1000\nuniform float Smoothness = 0.5; // Smoothness : (smoothness), min=-1000, max=1000\nuniform float Shape = 0.7; // Shape : (shape), min=-3, max=3\nuniform float Zoom = 1.0; // Zoom : (zoom), min=0.00001, max=10\nuniform float Layer = 8.0; // Layer : (layer), min=1.0, max=100.0\n\nuniform vec3 Blobs = vec3(0.0,0.2,0.3);\nuniform vec3 Depth = vec3(1,1,1);\n\nvec2 Resolution = vec2(iResolution.x * Zoom, iResolution.y * Zoom);\nfloat Time = iTime *-.05 * Speed + Offset;\n\n\n\nuniform vec2 Camera;\n\n\nconst float pi = 3.14159;\n\nvec3 rotate(vec3 v,vec2 r) \n{\n\tmat3 rxmat = mat3(1,   0    ,    0    ,\n\t\t\t  0,cos(r.y),-sin(r.y),\n\t\t\t  0,sin(r.y), cos(r.y));\n\tmat3 rymat = mat3(cos(r.x), 0,-sin(r.x),\n\t\t\t     0    , 1,    0    ,\n\t\t\t  sin(r.x), 0,cos(r.x));\n\t\n\t\n\treturn v*rxmat*rymat;\n\t\n}\n\nfloat snoise(vec3 v);\n\n//\n// Description : Array and textureless GLSL 2D/3D/4D simplex \n//               noise functions.\n//      Author : Ian McEwan, Ashima Arts.\n//  Maintainer : ijm\n//     Lastmod : 20110822 (ijm)\n//     License : Copyright (C) 2011 Ashima Arts. All rights reserved.\n//               Distributed under the MIT License. See LICENSE file.\n//               https://github.com/ashima/webglse-noi\n// \n\nvec3 mod289(vec3 x) {\n  return x - floor(x * (1.0 / 289.0)) * 289.0;\n}\n\nvec4 mod289(vec4 x) {\n  return x - floor(x * (1.0 / 289.0)) * 289.0;\n}\n\nvec4 permute(vec4 x) {\n     return mod289(((x*34.0)+1.0)*x);\n}\n\nvec4 taylorInvSqrt(vec4 r)\n{\n  return 1.79284291400159 - 0.85373472095314 * r;\n}\n\nfloat snoise(vec3 v)\n  { \n  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;\n  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);\n\n// First corner\n  vec3 i  = floor(v + dot(v, C.yyy) );\n  vec3 x0 =   v - i + dot(i, C.xxx) ;\n\n// Other corners\n  vec3 g = step(x0.yzx, x0.xyz);\n  vec3 l = 1.0 - g;\n  vec3 i1 = min( g.xyz, l.zxy );\n  vec3 i2 = max( g.xyz, l.zxy );\n  vec3 x1 = x0 - i1 + C.xxx;\n  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y\n  vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y\n\n// Permutations\n  i = mod289(i); \n  vec4 p = permute( permute( permute( \n             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))\n           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) \n           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));\n\n// Gradients: 7x7 points over a square, mapped onto an octahedron.\n// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)\n  float n_ = 0.142857142857; // 1.0/7.0\n  vec3  ns = n_ * D.wyz - D.xzx;\n\n  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)\n\n  vec4 x_ = floor(j * ns.z);\n  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)\n\n  vec4 x = x_ *ns.x + ns.yyyy;\n  vec4 y = y_ *ns.x + ns.yyyy;\n  vec4 h = 1.0 - abs(x) - abs(y);\n\n  vec4 b0 = vec4( x.xy, y.xy );\n  vec4 b1 = vec4( x.zw, y.zw );\n\n  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;\n  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;\n  vec4 s0 = floor(b0)*2.0 + 1.0;\n  vec4 s1 = floor(b1)*2.0 + 1.0;\n  vec4 sh = -step(h, vec4(0.0));\n\n  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;\n  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;\n\n  vec3 p0 = vec3(a0.xy,h.x);\n  vec3 p1 = vec3(a0.zw,h.y);\n  vec3 p2 = vec3(a1.xy,h.z);\n  vec3 p3 = vec3(a1.zw,h.w);\n\n//Normalise gradients\n  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));\n  p0 *= norm.x;\n  p1 *= norm.y;\n  p2 *= norm.z;\n  p3 *= norm.w;\n\n// Mix final noise value\n  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);\n  m = m * m;\n  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), \n                                dot(p2,x2), dot(p3,x3) ) );\n  }\n  \nvoid mainImage( out vec4 fragColor, in vec2 fragCoord )\n{\n\n  \tvec2 res = vec2(Resolution.x/Resolution.y,1.0);\n  \tvec2 p = ( fragCoord.xy / Resolution.y ) -(res/2.0);\n\t\n  \tvec2 m = (Camera-0.5)*pi*vec2(2.,1.);\n\t\n  \tvec3 color = vec3(0.0);\n\t\n  \tvec3 pos = normalize(rotate(vec3(p,0.5),vec2(m)));\n\t\n  \tfloat dist = 0.0;\n\t\n  \tfor(float i = 1.;i <= Layer;i++)\n  \t{\n  \t\tfloat shell = abs(snoise(pos*i+vec3(Time,0,0)*0.3));\n\t\t\n  \t\tshell = smoothstep(0.5 * Smoothness,0.6 * Shape,shell);\n\t\t\n  \t\tdist = max(dist,shell*(1.-(i/Layer)));\n  \t}\n\t\n  \tcolor = mix(vec3(Blobs),vec3(Depth),1.-dist);\n\t\t\n  \tfragColor = vec4(  color.xyz , 1.0 );\n\n  }\n\n\n  ")
